import React, {Component} from 'react';
import {CSSTransitionGroup} from 'react-transition-group'; // ES6
import '../../styles/login-page/loginpage.css';
import firebase from 'firebase';
import PropTypes from 'prop-types'
import { compose } from 'redux'
import { connect } from 'react-redux'
import { firebaseConnect, isLoaded, isEmpty } from 'react-redux-firebase'

const widthWindow = window.innerWidth
const heightWindow = window.innerHeight

const auth = firebase.auth;

var provider = new firebase.auth.GoogleAuthProvider();

class LoginPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
        }
    }


    render() {
        return ( 
            <div className="container-loginpage" style={{"width": widthWindow, "height":heightWindow}}>
                <CSSTransitionGroup 
                transitionName="aminMoveDown"
                transitionAppear={true}
                transitionAppearTimeout={800}
                transitionEnterTimeout={0}
                transitionLeaveTimeout={300}>
                <div className="main" >
                    <CSSTransitionGroup 
                        transitionName="aminAppear"
                        transitionAppear={true}
                        transitionAppearTimeout={800}
                        transitionEnterTimeout={0}
                        transitionLeaveTimeout={300}>
                        <h1 className="title-login">Signin</h1>
                    </CSSTransitionGroup>
                    <div className="btn-login-wrapper">
                        <button class="btn-login-google"onClick={() => firebase.login({ provider: 'google', type: 'popup' })}>
                            Sign in Google
                        </button>
                    </div>
                </div>
                </CSSTransitionGroup>
            </div>
        );
    }
}

export default LoginPage;
